nce()->getConnection()->query($querypart);\n		 // For all participants in this query set participated to true\n	while ($row = $res_part->fetch_assoc()) { \n		 	// Curl transit from prepared to participated\n		$part_id = $rest = substr($row[\'idnumber\'], 0, -3);\n		// Get the coms_participant_exam_event_id for each participant\n		$query_part_ex = \"SELECT coms_participant_exam_event_id FROM bpmspace_coms_v1_A.coms_participant_exam_event where coms_participant_id = $part_id and coms_exam_event_id = $event_id LIMIT 1\";\n		$res_part_ex = DB::getInstance()->getConnection()->query($query_part_ex);\n		$rowpart = $res_part_ex->fetch_assoc();\n\n		$data = json_encode(array(\'cmd\' => \'makeTransition\', \'paramJS\' => array(\'table\' => \'coms_participant_exam_event\', \'row\' => array(\'coms_participant_exam_event_id\' => $rowpart[\'coms_participant_exam_event_id\'], \'state_id\' => 29))));\n		//$message.= \"after participated\";\n\n				// Change state through API\n		$ch = curl_init();\n\n		curl_setopt($ch, CURLOPT_URL,\"$COMS_PATH\");\n		curl_setopt($ch, CURLOPT_POST, 1);\n		curl_setopt($ch, CURLOPT_POSTFIELDS, $data);\n\n		// receive server response ...\n		curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n\n		curl_setopt($ch, CURLOPT_VERBOSE, true);\n\n		$verbose = fopen(\'php://temp\', \'w+\');\n		curl_setopt($ch, CURLOPT_STDERR, $verbose);\n\n		# sending manually set cookie\n		curl_setopt($ch, CURLOPT_HTTPHEADER, array(\"Cookie: token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJ1aWQiOjEzMzcsImZpcnN0bmFtZSI6Ik1hY2hpbmUiLCJsYXN0bmFtZSI6Ik1hY2hpbmUifQ.f4rVRRn0x8NC38PVYxUqwKdhLCdOk9WRlZ53xrFmgUs\"));\n	\n		$result = curl_exec($ch);\n		if ($result === FALSE) {\n		    printf(\"cUrl error (#%d): %s<br>\\n\", curl_errno($ch),\n		           htmlspecialchars(curl_error($ch)));\n		}\n		rewind($verbose);\n		$verboseLog = stream_get_contents($verbose);\n\n		curl_close ($ch);\n	}\n		 // For all not participated / not in the query but in coms set to not participated\n	$querynot =  \"SELECT \n    *\n	FROM\n	bpmspace_coms_v1_A.coms_participant_exam_event\n	LEFT JOIN\n	(SELECT \n	f.idnumber\n	FROM\n	moodle.mdl_offlinequiz_results e\n	JOIN moodle.mdl_user f ON e.userid = f.id) AS a ON LEFT(a.idnumber, LENGTH(a.idnumber) - 3) = bpmspace_coms_v1_A.coms_participant_exam_event.coms_participant_id\n	where bpmspace_coms_v1_A.coms_participant_exam_event.coms_exam_event_id = \'$event_id\' and a.idnumber IS NULL\";\n	//$message.= \"Querynot: $querynot\";\n\n	$res_not = DB::getInstance()->getConnection()->query($querynot);\n	while ($row = $res_not->fetch_assoc()) { \n	// Curl transit from prepared to participated\n		$data = json_encode(array(\'cmd\' => \'makeTransition\', \'paramJS\' => array(\'table\' => \'coms_participant_exam_event\', \'row\' => array(\'coms_participant_exam_event_id\' => $row[\'coms_participant_exam_event_id\'], \'state_id\' => 30))));\n\n\n			// Change state through API\n		$ch = curl_init();\n\n		curl_setopt($ch, CURLOPT_URL,\"$COMS_PATH\");\n		curl_setopt($ch, CURLOPT_POST, 1);\n		curl_setopt($ch, CURLOPT_POSTFIELDS, $data);\n\n		// receive server response ...\n		curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n\n		curl_setopt($ch, CURLOPT_VERBOSE, true);\n\n		$verbose = fopen(\'php://temp\', \'w+\');\n		curl_setopt($ch, CURLOPT_STDERR, $verbose);\n\n		# sending manually set cookie\n		curl_setopt($ch, CURLOPT_HTTPHEADER, array(\"Cookie: token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJ1aWQiOjEzMzcsImZpcnN0bmFtZSI6Ik1hY2hpbmUiLCJsYXN0bmFtZSI6Ik1hY2hpbmUifQ.f4rVRRn0x8NC38PVYxUqwKdhLCdOk9WRlZ53xrFmgUs\"));\n	\n		$result = curl_exec($ch);\n		if ($result === FALSE) {\n		    printf(\"cUrl error (#%d): %s<br>\\n\", curl_errno($ch),\n		           htmlspecialchars(curl_error($ch)));\n		}\n		rewind($verbose);\n		$verboseLog = stream_get_contents($verbose);\n\n		curl_close ($ch);\n\n//$message.= \"After not participated\";\n}\n\n\n$res = DB::getInstance()->getConnection()->query(\"SELECT * From coms_participant_exam_event Where coms_exam_event_id = \'$event_id\' \");\n// Get each individual participant\nwhile ($row = $res->fetch_assoc()) {\n\n	$id = $row[\'coms_participant_exam_event_id\'];\n	$state = $row[\'state_id\'];\n	$message.=\"State before: $state\";\n	// If sate is participated\n	if ($state == 29){\n		// If succ or failed\n\n		$userid = $row[\'coms_participant_id\'];\n		\n		// Query get all propertys of joined coms and moodle\n		$query2 = \"SELECT \n			    *\n			FROM\n			    ((moodle.mdl_offlinequiz_results\n			    JOIN moodle.mdl_user ON moodle.mdl_offlinequiz_results.userid = moodle.mdl_user.id)\n			    JOIN moodle.mdl_offlinequiz ON moodle.mdl_offlinequiz_results.offlinequizid = moodle.mdl_offlinequiz.id)\n			        JOIN\n			    bpmspace_coms_v1_A.coms_exam_event ON moodle.mdl_offlinequiz.name LIKE CONCAT(\'%\',\n			            bpmspace_coms_v1_A.coms_exam_event.coms_exam_event_id_base32,\n			            \'%\')\n			        INNER JOIN\n			    bpmspace_coms_v1_A.coms_exam ON bpmspace_coms_v1_A.coms_exam_event.coms_exam_id = bpmspace_coms_v1_A.coms_exam.coms_exam_id\n			WHERE\n			    bpmspace_coms_v1_A.coms_exam_event.coms_exam_event_id = \'876072\'\n			        AND LEFT(moodle.mdl_user.idnumber,\n			        LENGTH(moodle.mdl_user.idnumber) - 3) = \'$userid\'\";\n\n		$res_query2 = DB::getInstance()->getConnection()->query($query2);\n		$row2 = $res_query2->fetch_array();\n		$passmark = $row2[\'coms_exam_pass_mark\'];\n		$points = $row2[\'sumgrades\'];\n		//$message.=\"Vor überprüfung auf bestanden und nicht bestanden\";\n		// TODO: Fill coms_participant_exam_event_percent and coms_participant_info in coms_participant_exam_event with values from moodle.\n		if ($points > $passmark) {\n			$message .= \" Bestanden\";\n\n			$data = json_encode(array(\'cmd\' => \'makeTransition\', \'paramJS\' => array(\'table\' => \'coms_participant_exam_event\', \'row\' => array(\'coms_participant_exam_event_id\' => $id, \'state_id\' => 31))));\n		}\n		else {\n			$message .= \" Nicht bestanden\";\n			$data = json_encode(array(\